import com.eviware.soapui.support.XmlHolder

def addrequest = testRunner.testCase.testSuite.project.testSuites["Customer"].testCases["CustomerSave"].testSteps["CustomerSave"].getPropertyValue("request")
log.info addrequest

def holder = new XmlHolder(addrequest)

//to set the value in the request
holder.setNodeValue("//*:customer/email","meenu@gmail.com")
holder.setNodeValue("//*:customer/firstname","meenu")
holder.setNodeValue("//*:customer/lastname","pandey")

//i want to save the changes made
def newxml = holder.getXml()

testRunner.testCase.testSuite.project.testSuites["Customer"].testCases["CustomerSave"].testSteps["CustomerSave"].setPropertyValue("request",newxml)

def tc = testRunner.testCase.testSuite.project.testSuites["Customer"].testCases["CustomerSave"]

tc.run (null,false)

